    const price = `$${props.price.toFixed(2)}`; // two decimals
    return (
        <li className={styles.meal}>
            {/*Meal Info*/}
          <div>
            <h3>{props.name}</h3>
            <div className = {styles.price}>{price}</div>
          </div>
        </li>
)};

export default CartItem;
